export const homepageTheme = {
  darkSection: {
    backgroundColor: theme.palette.primary.dark,
    color: theme.palette.primary.light,
    padding: "8% 4%",
    maxWidth: "100%",
  },
  image: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
    zIndex: "2",
    position: "relative",
    boxShadow:
      "0 2.8px 2.2px rgba(0, 0, 0, 0.02), 0 6.7px 5.3px rgba(0, 0, 0, 0.028), 0 12.5px 10px rgba(0, 0, 0, 0.035), 0 22.3px 17.9px rgba(0, 0, 0, 0.042), 0 41.8px 33.4px rgba(0, 0, 0, 0.05), 0 100px 80px rgba(0, 0, 0, 0.07)",
  },
  imageContainer: {
    width: "100%",
    position: "relative",
    zIndex: "2",
  },
  imageSm: {
    position: "absolute",
    zIndex: "1",
    left: "-60%",
    transform: "rotate(140deg)",
    bottom: "-50%",
  },
  h1: {
    fontSize: "3.2rem",
    lineHeight: 1.6,
    textAlign: "center",
  },
  h2: {
    color: theme.palette.primary.main,
    marginTop: "2rem",
  },
  h4: {
    color: theme.palette.primary.main,
    fontWeight: 600,
    marginTop: "2rem",
  },
};
