import React, { useState } from "react";
import PropTypes from "prop-types";
import { Box, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import heroImg from "../../assets/images/hero-img.png";
import blob from "../../assets/images/blob-shape.svg";
import blob2 from "../../assets/images/blob-shape-4.svg";
import { animated, useSprings } from "react-spring";

function Hero(props) {
  const useStyles = makeStyles((theme) => ({
    root: {
      backgroundColor: theme.palette.primary.light,
      height: "100%",
      minHeight: "100vh",
      zIndex: "-1",
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      padding: "4%",
      maxWidth: "80%",
      margin: "auto",
    },
    imgContainer: {
      position: "relative",
    },
    image: {
      position: "absolute",
      top: "-34%",
      left: "-26%",
      zIndex: "1",
      height: 300,
    },
    image2: {
      position: "absolute",
      bottom: "-74%",
      right: "-56%",
      zIndex: "1",
      transform: "rotate(45deg)",
      height: 600,
    },
    imageLg: {
      height: 400,
      position: "relative",
      width: "auto",
      zIndex: "2",
    },
    h2: {
      width: "100%",
    },
  }));
  const classes = useStyles();
  const copy = [
    {
      variant: "h1",
      component: "h1",
      text: "Charlotte's first true seltzery.",
      className: "",
      key: 1,
    },
    {
      variant: "h2",
      component: "h4",
      text: "Coming to you summer 2020",
      className: classes.h2,
      key: 2,
    },
  ];

  const [items, setItem] = useState(copy);
  console.log(items);
  const [springs, set, stop] = useSprings(items, (item) => ({
    transform: "translate3d(0,-40px,0)",
  }));

  set((item) => ({ transform: "translate3d(0,0px,0)" }));

  const transitionGroup = springs.map(({ item, props, key }) => (
    <animated.div key={key} style={props}>
      <Typography
        variant={item.variant}
        component={item.component}
        className={item.className}
      >
        {item.text}
      </Typography>
    </animated.div>
  ));

  return (
    <Box
      display="flex"
      justifyContent="space-around"
      alignItems="center"
      flexWrap="nowrap"
      alignContent="center"
      textAlign="center"
      className={classes.root}
    >
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
        alignContent="center"
        textAlign="left"
        maxWidth="30%"
      >
        {transitionGroup}
      </Box>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
        alignContent="center"
        textAlign="center"
      >
        <div className={classes.imgContainer}>
          <img src={blob} className={classes.image} />
          <img
            src={heroImg}
            alt="People on a porch"
            className={classes.imageLg}
          />
          <img src={blob2} className={classes.image2} />
        </div>
      </Box>
    </Box>
  );
}

Hero.propTypes = {};

export default Hero;
