import React, { useState } from "react";
import PropTypes from "prop-types";
import { Box, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import heroImg from "../../assets/images/hero-img.png";
import Blob1 from "../../assets/images/blob-shape";
import Blob2 from "../../assets/images/blob-shape-2";

function Hero(props) {
  const useStyles = makeStyles((theme) => ({
    root: {
      backgroundColor: theme.palette.primary.light,
      height: "calc(100vh - 320px)",
      zIndex: "2",
      padding: "8% 8% 10%",
      maxWidth: "80%",
      margin: "4% auto 0 auto",
      position: "relative",
    },
    imgContainer: {
      position: "absolute",
      borderRadius: "4px",
      right: "10%",
      transform: "translate3D(0, -40px, 0)",
    },
    image: {
      position: "absolute",
      top: "-34%",
      left: "-26%",
      zIndex: "1",
      height: 300,
      borderRadius: "4px",
    },
    image2: {
      position: "absolute",
      right: "-26%",
      bottom: "-100%",
      zIndex: "1",
      transform: "rotate(45deg)",
      height: 600,
    },
    imageLg: {
      position: "relative",
      height: "24rem",
      width: "auto",
      borderRadius: "4px",
      zIndex: "2",
      boxShadow:
        "0 2.8px 2.2px rgba(0, 0, 0, 0.02), 0 6.7px 5.3px rgba(0, 0, 0, 0.028), 0 12.5px 10px rgba(0, 0, 0, 0.035), 0 22.3px 17.9px rgba(0, 0, 0, 0.042), 0 41.8px 33.4px rgba(0, 0, 0, 0.05), 0 100px 80px rgba(0, 0, 0, 0.07)",
    },
    h4: {
      width: "100%",
      marginTop: 32,
    },
  }));
  const classes = useStyles();
  return (
    <Box
      display="flex"
      justifyContent="space-between"
      alignItems="center"
      flexWrap="nowrap"
      alignContent="center"
      textAlign="center"
      className={classes.root}
    >
      <Box
        display="flex"
        justifyContent="flex-start"
        alignItems="center"
        flexWrap="wrap"
        alignContent="center"
        textAlign="left"
        maxWidth="30%"
      >
        <Typography variant="h1" component="h1">
          Charlotte's first true seltzery.
        </Typography>
        <Typography variant="h4" component="h2" className={classes.h4}>
          Coming to you summer 2020
        </Typography>
      </Box>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
        alignContent="center"
        textAlign="center"
      >
        <div className={classes.imgContainer}>
          <Blob1 className={classes.image} />
          <img
            src={heroImg}
            alt="People on a porch"
            className={classes.imageLg}
          />
          <Blob2 className={classes.image2} />
        </div>
      </Box>
    </Box>
  );
}

Hero.propTypes = {};

export default Hero;
