import React from "react";
import PropTypes from "prop-types";
import { animated, useSpring } from "react-spring";

const AnimFeTurbulence = animated("feTurbulence");
const AnimFeDisplacementMap = animated("feDisplacementMap");

function Blob1(props) {
  const { freq, scale, transform, opacity } = useSpring({
    reverse: true,
    from: {
      scale: 10,
      opacity: 0,
      transform: "scale(0.9)",
      freq: "0.0175, 0.0",
    },
    to: { scale: 150, opacity: 1, transform: "scale(1)", freq: "0.0, 0.0" },
    config: { duration: 3000 },
  });
  return (
    <div className={props.className}>
      <animated.svg viewBox="0 0 600 600" style={{ transform, opacity }}>
        <defs>
          <filter id="water">
            <AnimFeTurbulence
              type="fractalNoise"
              baseFrequency={freq}
              numOctaves="1.5"
              result="TURB"
              seed="8"
            />
            <AnimFeDisplacementMap
              xChannelSelector="R"
              yChannelSelector="G"
              in="SourceGraphic"
              in2="TURB"
              result="DISP"
              scale={scale}
            />
          </filter>
        </defs>
        <g transform="translate(300,300)" filter="url(#water)">
          <animated.path
            style="fill:#d3972c"
            d="M198.7,7.5C198.7,90,99.3,180,5,180C-89.3,180,-178.7,90,-178.7,7.5C-178.7,-75,-89.3,-150,5,-150C99.3,-150,198.7,-75,198.7,7.5Z"
            fill="#FFB4BC"
          />
        </g>
      </animated.svg>
    </div>
  );
}

Blob1.propTypes = {};

export default Blob1;
