import React, { useState } from "react";
import PropTypes from "prop-types";
import { useMediaQuery } from "../../utils/mediaQueryHook";
import { Box, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import heroImg from "../../assets/images/hero-image.png";
import Blob1 from "../../assets/images/small-blob.png";
import Blob2 from "../../assets/images/long-blob.png";
import "./hero.scss";

function Hero(props) {
  const tablet = useMediaQuery("(max-width: 991px)");
  const useStyles = makeStyles((theme) => ({
    root: {
      backgroundColor: theme.palette.primary.light,
    },
    imgContainer: {
      borderRadius: "4px",
      width: "92%",
      maxHeight: "84%",
      position: "relative",
    },
    image: {
      borderRadius: "4px",
      position: "absolute",
      left: "-8%",
      top: "-10%",
    },
    image2: {
      position: "absolute",
      right: "-30%",
      bottom: "-32%",
      zIndex: "1",
      height: "65%",
    },
    fillImage: {
      height: "100%",
      width: "100%",
      objectFit: "cover",
      zIndex: 2,
      position: "relative",
    },
    h4: {
      width: "100%",
      marginTop: 32,
    },
  }));
  const classes = useStyles();
  return (
    <Box className={classes.root}>
      <Box className="content-right">
        <Typography variant="h1" component="h1">
          Charlotte's first true seltzery.
        </Typography>
        <Typography variant="h4" component="h2" className={classes.h4}>
          Coming to you summer 2020
        </Typography>
      </Box>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
        alignContent="center"
        textAlign="center"
        maxWidth={tablet ? "100%" : "50%"}
      >
        <div className={classes.imgContainer}>
          <img src={Blob1} className={classes.image} />
          <img
            src={heroImg}
            alt="People on a porch"
            className={classes.fillImage}
          />
          <img src={Blob2} className={classes.image2} />
        </div>
      </Box>
    </Box>
  );
}

Hero.propTypes = {};

export default Hero;
