import React from "react";
import PropTypes from "prop-types";
import { Hero } from "../../ui-components";
import { Box, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import section2Image from "../../assets/images/coffee-cups.png";

function Home(props) {
  const useStyles = makeStyles((theme) => ({
    darkSection: {
      backgroundColor: theme.palette.primary.dark,
      color: theme.palette.primary.light,
      height: "100%",
      padding: "4%",
      maxWidth: "100%",
      overflow: "hidden",
    },
    image: {
      width: "100%",
      height: "100%",
      objectFit: "cover",
    },
    imageContainer: {
      width: "100%",
    },
  }));
  const classes = useStyles();
  return (
    <>
      <Hero />
      <Box className={classes.darkSection}>
        <Box
          display="flex"
          flexWrap="wrap"
          alignItems="center"
          justifyContent="center"
          maxWidth="80%"
          margin="auto"
        >
          <Box display="flex" flexDirection="column" width="40%">
            <Typography>
              We believe being healthy and having fun can go hand in hand. Which
              is why we created a space for everyone to enjoy our
              house-fermented, crystal-clear hard seltzer together. It's like a
              brewery but for seltzer. A seltzery.
            </Typography>
            <Box className={classes.imageContainer} marginTop="4%">
              <img src={section2Image} className={classes.image}></img>
            </Box>
          </Box>
          <Box width="40%"></Box>
        </Box>
      </Box>
    </>
  );
}

Home.propTypes = {};

export default Home;
