export const homepageTheme = (theme) => {
  return {
    button: {
      backgroundColor: theme.palette.primary.main,
      color: theme.palette.primary.light,
      letterSpacing: 1.2,
      fontWeight: "bold",
      fontSize: "16px",
      width: 200,
    },
    darkSection: {
      backgroundColor: theme.palette.primary.dark,
      color: theme.palette.primary.light,
      padding: "8%",
      maxWidth: "100%",
    },
    imgTop: {
      width: "auto",
      display: "block",
    },
    imgBottom: {
      width: "auto",
      float: "right",
      display: "block",
      transform: "translate3d(-34%, -12%, 0)",
    },

    ssStamp: {
      height: "180px",
      width: "auto",
      position: "absolute",
      right: "0",
      top: "-14%",
    },
    imageSm: {
      position: "absolute",
      zIndex: "1",
      left: "-60%",
      transform: "rotate(140deg)",
      bottom: "-50%",
    },
    list: {
      margin: "8% 0 0",
    },
    listItem: {
      margin: "3% 0 0",
    },
    listItemTitle: {
      marginBottom: "1%",
    },

    h1: {
      fontSize: "3.2rem",
      lineHeight: 1.6,
      textAlign: "center",
    },
    header1: {
      fontSize: "5rem",
    },
    h2: {
      color: theme.palette.primary.main,
      marginTop: "2rem",
    },
    h3: {
      margin: "6rem 0 2rem 0",
    },
    h4: {
      color: theme.palette.primary.main,
      fontWeight: 600,
      marginTop: "2rem",
    },
  };
};
