import React from "react";
import PropTypes from "prop-types";
import { Box, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import heroImg from "../../assets/images/hero-img.png";
import blob from "../../assets/images/blob-shape.svg";
function Hero(props) {
  const useStyles = makeStyles((theme) => ({
    root: {
      backgroundColor: theme.palette.primary.light,
      height: "100%",
      minHeight: "100vh",
      zIndex: "-1",
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      padding: "4%",
      maxWidth: "80%",
      margin: "auto",
    },
    image: {
      height: "400px",
      width: "auto",
    },
    h2: {
      width: "100%",
    },
  }));
  const classes = useStyles();

  return (
    <Box
      display="flex"
      justifyContent="space-around"
      alignItems="center"
      flexWrap="nowrap"
      alignContent="center"
      textAlign="center"
      className={classes.root}
    >
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
        alignContent="center"
        textAlign="left"
        maxWidth="30%"
      >
        <Typography variant="h1" component="h1">
          Charlotte's first true seltzery.
        </Typography>
        <Typography variant="h2" component="h4" className={classes.h2}>
          Coming to you summer 2020
        </Typography>
      </Box>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
        alignContent="center"
        textAlign="center"
      >
        <div className={classes.imgContainer}>
          <img src={blob} />
          <img
            src={heroImg}
            alt="People on a porch"
            className={classes.image}
          />
        </div>
      </Box>
    </Box>
  );
}

Hero.propTypes = {};

export default Hero;
