import React from "react";
import PropTypes from "prop-types";
import { Hero } from "../../ui-components";
import { Box, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import section2Image from "../../assets/images/coffee-cups.png";

function Home(props) {
  const useStyles = makeStyles((theme) => ({
    darkSection: {
      backgroundColor: theme.palette.primary.dark,
      color: theme.palette.primary.light,
      height: "100%",
      padding: "4%",
      maxWidth: "100%",
    },
    image: {
      width: "auto",
      height: 600,
    },
  }));
  const classes = useStyles();
  return (
    <>
      <Hero />
      <Box className={classes.darkSection}>
        <Box display="flex" flexDirection="column" width="50%">
          <Typography>
            We believe being healthy and having fun can go hand in hand. Which
            is why we created a space for everyone to enjoy our house-fermented,
            crystal-clear hard seltzer together. It's like a brewery but for
            seltzer. A seltzery.
          </Typography>
          <div>
            <img src={section2Image} className={classes.image}></img>
          </div>
        </Box>
        <Box></Box>
      </Box>
    </>
  );
}

Home.propTypes = {};

export default Home;
