import React, { useState } from "react";
import PropTypes from "prop-types";
import { Box, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import heroImg from "../../assets/images/hero-img.png";
import Blob1 from "../../assets/images/blob-shape";
import Blob2 from "../../assets/images/blob-shape-2";
import { ParallaxLayer } from "react-spring/renderprops-addons";
import Parallax from "../ParallaxContainer";
import { useSpring, animated } from "react-spring";

function Hero(props) {
  const useStyles = makeStyles((theme) => ({
    root: {
      backgroundColor: theme.palette.primary.light,
      height: "calc(100vh - 320px)",
      zIndex: "2",
      padding: "8% 4% 14%",
      maxWidth: "80%",
      margin: "4% auto 0 auto",
      position: "relative",
    },
    imgContainer: {
      position: "absolute",
      borderRadius: "4px",
      right: "10%",
      transform: "translate3D(0, -40px, 0)",
    },
    image: {
      position: "absolute",
      top: "-34%",
      left: "-26%",
      zIndex: "1",
      height: 300,
      borderRadius: "4px",
    },
    image2: {
      position: "absolute",
      right: "-26%",
      top: "-20%",
      zIndex: "1",
      transform: "rotate(45deg)",
      height: 600,
    },
    imageLg: {
      position: "relative",
      height: "24rem",
      width: "auto",
      borderRadius: "4px",
      filter: "blur(4px)",

      zIndex: "2",
      boxShadow:
        "0 2.8px 2.2px rgba(0, 0, 0, 0.02), 0 6.7px 5.3px rgba(0, 0, 0, 0.028), 0 12.5px 10px rgba(0, 0, 0, 0.035), 0 22.3px 17.9px rgba(0, 0, 0, 0.042), 0 41.8px 33.4px rgba(0, 0, 0, 0.05), 0 100px 80px rgba(0, 0, 0, 0.07)",
    },
    h4: {
      width: "100%",
      marginTop: 32,
    },
  }));
  const classes = useStyles();
  const copy = [
    {
      variant: "h1",
      component: "h1",
      text: "Charlotte's first true seltzery.",
      className: "",
      key: 1,
    },
    {
      variant: "h2",
      component: "h4",
      text: "Coming to you summer 2020",
      className: classes.h2,
      key: 2,
    },
  ];

  const imgFadeIn = useSpring({
    filter: "blur(0px)",
    transform: "translate3D(0, 0, 0)",
  });

  //   const [items, setItem] = useState(copy);
  //   console.log(items);
  //   const [springs, set, stop] = useSprings(items.length, items.map(item => ({
  //     transform: "translate3d(0,-40px,0)",
  //   }))

  //   const transitionGroup = springs.map(({ item, props, key }) => (
  //     <animated.div key={key} style={props}>
  //       <Typography
  //         variant={item.variant}
  //         component={item.component}
  //         className={item.className}
  //       >
  //         {item.text}
  //       </Typography>
  //     </animated.div>
  //   ));

  return (
    <Box
      display="flex"
      justifyContent="flex-end"
      alignItems="center"
      flexWrap="nowrap"
      alignContent="center"
      textAlign="center"
      width="100%"
      className={classes.root}
    >
      <ParallaxLayer
        offset={0.34}
        speed={0.1}
        factor={1}
        style={{ width: "80%" }}
      >
        <Box
          display="flex"
          justifyContent="flex-start"
          alignItems="center"
          flexWrap="wrap"
          alignContent="center"
          textAlign="left"
          maxWidth="30%"
        >
          <Typography variant="h1" component="h1">
            Charlotte's first true seltzery.
          </Typography>
          <Typography variant="h4" component="h2" className={classes.h4}>
            Coming to you summer 2020
          </Typography>
        </Box>
      </ParallaxLayer>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
        alignContent="center"
        textAlign="center"
      >
        <div className={classes.imgContainer}>
          <ParallaxLayer offset={0.2} speed={0.1} factor={1}>
            <Blob1 className={classes.image} />
          </ParallaxLayer>
          <animated.img
            style={imgFadeIn}
            src={heroImg}
            alt="People on a porch"
            className={classes.imageLg}
          />
          <ParallaxLayer offset={"0"} speed={0.05} factor={1}>
            <Blob2 className={classes.image2} />
          </ParallaxLayer>
        </div>
      </Box>
    </Box>
  );
}

Hero.propTypes = {};

export default Hero;
